mapper.select:
SELECT product_id, product_name, product_description, product_category, product_price, product_image, product_sales_volume, product_posted_at
FROM product
LIMIT #{offset}, #{pageSize}

SELECT product_id, product_name, product_description, product_category, product_price, product_image, product_sales_volume, product_posted_at FROM product
product_category = #{product_category}
ORDER BY
product_posted_at product_name product_price product_sales_volume
ASC DESC
LIMIT #{offset}, #{pageSize}
